import React, {useState} from "react";
import {RouteD3Tree} from "./RouteD3Tree";

export const Route = ({route}) => {
    const [show, setShow] = useState(false);

    const handleClick = () => {
        setShow(show => !show)
    };

    return (
        <div style={{display:"grid", gridTemplateColumns:"repeat(5, 1fr)", gridTemplateRows:"repeat(5, 1fr)"}}>
            <div>
            <button onClick={handleClick}>{route.name}</button>
            </div>
            <div>
            {show && <RouteD3Tree route={route} show={show}/>}
            </div>
        </div>
    );
};
