import React, {useEffect, useState, useRef} from "react";
import Tree from 'react-d3-tree';
import {useStyles} from "./Main";

const svgSize = {x: 200, y: 200}
const MoleculeNode = ({nodeDatum}) => (
    /* Display HTML within the node by leveraging the foreignObject tag.
    See this example for details: https://codesandbox.io/s/rd3t-v2-custom-with-foreignobject-0mfj8?file=/src/App.js
    */
    <g>
        {/* `foreignObject` requires width & height to be explicitly set. */}
        <foreignObject width={svgSize.x} height={svgSize.y} x={-100}>
            <div style={{ border: "1px solid black", backgroundColor: "#dedede"}}>
                <div style={{ textAlign: "center", wordWrap: "break-word"}}>{nodeDatum.name}</div>
                <div dangerouslySetInnerHTML={{ __html: nodeDatum.attributes.svg }} />
            </div>
        </foreignObject>
    </g>
);

const ReactionNode = ({nodeDatum}) => (
    <g>
        <foreignObject width={svgSize.x} height={svgSize.y} x={-100}>
            <div style={
                {border: "1px solid black",
                    backgroundColor: "#dedede",
                    textAlign: "center",
                    wordWrap: "break-word"
                }
            }>
                {nodeDatum.name}
            </div>
        </foreignObject>
    </g>
);


export const RouteD3Tree = ({route}) => {
    const styles = useStyles();

    const [rootData, setRootData] = useState(null);
    const [translate, setTranslate] = useState({ x: svgSize.x/2, y: svgSize.y/8});

    const treeContainerRef = useRef();

    useEffect(() => {
        if (route) {
            const fetchRootData = async () => {
                const response = await fetch(`http://localhost:8000/routes/${route.id}`);
                const data = await response.json();
                setRootData(data);
            };
            fetchRootData();
        }
    }, [route]);

    useEffect(()=>{
        if (treeContainerRef.current){
            const dimensions = treeContainerRef.current.getBoundingClientRect();
            setTranslate({
                x: dimensions.width * 0.15,
                y: dimensions.height * 0.05,
            });
        }
    }, [treeContainerRef.current]);

    const renderRectSvgNode = ({ nodeDatum, toggleNode }) => (
        nodeDatum.attributes.is_reaction ? (
        <ReactionNode nodeDatum={nodeDatum}/>
        ): <MoleculeNode nodeDatum={nodeDatum} />
    );
    return (
        rootData ? (
            <div
                id="treeWrapper"
                className={styles.noSpace}
                ref={treeContainerRef}
            >
                <Tree
                    id={route.id}
                    data={rootData}
                    nodeSize={svgSize}
                    translate={translate}
                    orientation="verticle"
                    renderCustomNodeElement={renderRectSvgNode}
                    dimentions={{width: svgSize.x, height: svgSize.y}}
                />
            </div>
        ) : null
    );
};
