import React, {useEffect, useState, useRef} from "react";
import Tree from 'react-d3-tree';
import {useStyles} from "./Main";


export const RouteD3Tree = ({route}) => {
    const styles = useStyles();

    const [rootData, setRootData] = useState(null);
    const [translate, setTranslate] = useState({ x:100, y: 24 });

    const treeContainerRef = useRef();

    useEffect(() => {
        if (route) {
            const fetchRootData = async () => {
                const response = await fetch(`http://localhost:8000/routes/${route.id}`);
                const data = await response.json();
                setRootData(data);
            };
            fetchRootData();
        }
    }, [route]);

    useEffect(()=>{
        if (treeContainerRef.current){
            const dimensions = treeContainerRef.current.getBoundingClientRect();
            setTranslate({
                x: dimensions.width * 0.15,
                y: dimensions.height * 0.05,
            });
        }
    }, [treeContainerRef.current]);

    const renderRectSvgNode = ({ nodeDatum, toggleNode }) => (
        <g>
            <rect width="20" height="20" x="-10" onClick={toggleNode} />
            <text fill="black" strokeWidth="1" x="20">
                {nodeDatum.name}
            </text>
            {nodeDatum.attributes?.svg && (
                <div dangerouslySetInnerHTML={{ __html: nodeDatum.attributes.svg }} />
            )}
        </g>
    );


    return (
        rootData ? (
            <div
                id="treeWrapper"
                className={styles.noSpace}
                ref={treeContainerRef}
            >
                <Tree
                    id={route.id}
                    data={rootData}
                    translate={translate}
                    orientation="vertical"
                    renderCustomNodeElement={renderRectSvgNode}
                />
            </div>
        ) : <></>
    );
};
